import axios from 'axios'
import constants from '../../plugins/constants'

const state = {
    clients:[],
    client:{id:1,uid:0,name:"",prenom:"",phone:""},
    status:false,
    session_caisse_id:1,
    objethold:{},
    statusrequete:false,
    holdlist:[],
    selecthold:{},
    listproducts:{},
    productshold:{},
    montantopen:0,
    boxopen:false,
    caisseopen:false,
    resource_url: constants.resource_url,
    removeallproducts:false,
};

const actions = {

    async FetchCustomers({commit}){

        axios.get(state.resource_url+'clients')
            .then(products => {
                console.log('clients',products.data.data);
                commit("SETCUSTOMERS",products.data.data)
            })
            .catch(function(error) {
                console.log('products_error',error);
                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);

            });

    },

    removeCatProducts({commit}, data) {
        commit("REMOVE_CAT_PRODUCTS", data);
    },

    async registerCustom({commit},data){

        data.idorganisation= "1"

         axios.post(state.resource_url+data.variant,data)

            .then(customers => {

                console.log('variant',customers)
                console.log('variant',customers.data)

                if(customers.data.success) {
                    //objet client qui vient detre ajoute
                    commit("SETCUSTOMER", customers.data.data)
                    commit("SETSTATUS", true)
                }
            })
            .catch(function(error) {
                console.log('customer_create_error',error);
                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);
                // ...
            });
        
        //let response = {id:1,uid:inputuid,name:inputName,prenom:inputPrenom,phone:inputPhone,birthday:birthday}
    },

    async addhold({commit}){

         axios.get(state.resource_url+"newhold/"+localStorage.getItem("sessioncaisse"))

            .then(holds => {
                if(holds.data.success) {
                    //objet client qui vient detre ajoute
                    commit("SETOBJETHOLD", holds.data.data)
                }
            })

            .catch(function(error) {
                console.log('customer_create_error',error);
                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);
                // ...
            });
    },

    async removehold({commit},data){

        data.sessionCaisseId  = localStorage.getItem("sessioncaisse")
        axios.post(state.resource_url+"deletehold",data)

            .then(holds => {

                if(holds.data.success) {
                    //objet client qui vient detre ajoute
                    commit("SETSTATUSHOLD", holds.data.success)
                }
            })

            .catch(function(error) {
                console.log('customer_create_error',error);
                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);
                // ...
            });
    },

    async FetchSalesProducts({commit},data){

        console.log('variant',data)
        axios.post(state.resource_url+"posales",data)

            .then(holds => {
                if(holds.data.success) {
                    //objet client qui vient detre ajoute
                    commit("SETSTATUSHOLD", holds.data.success)
                }

            })

            .catch(function(error) {
                console.log('customer_create_error',error);
                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);
                // ...
            });
    },

    async holdliste({commit},data){

        axios.get(state.resource_url+"holdlist/"+localStorage.getItem("sessioncaisse"))
            .then(holds => {

                if(holds.data.success) {
                    //objet client qui vient detre ajoute
                    commit("SETHOLDLIST", holds.data.data)
                    console.log('holdliste',holds.data.data)
                }

                if(data.id==1){
                    console.log('idlistproduct','d')
                    this.dispatch('getProductsHold',{number:(holds.data.data)[0]})
                }
            })

            .catch(function(error) {
                console.log('customer_create_error',error);
                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);
                // ...
            });
    },

    async SelectHolds({commit},data){

        axios.post(state.resource_url+"selecthold",{number:data.number,sessionCaisseId:localStorage.getItem("sessioncaisse")})

            .then(holds => {
                if(holds.data.success) {

                    //objet client qui vient detre ajoute
                    commit("SETSELECTHOLD", holds.data.data)

                }
            })

            .catch(function(error) {
                console.log('customer_create_error',error);
                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);
                // ...
            });
    },

    async getProductsHold({commit},data){

        axios.post(state.resource_url+"posaleslist",{number:data.number.number,sessionCaisseId:localStorage.getItem("sessioncaisse")})

            .then(holds => {
                if(holds.data.success) {

                    //objet client qui vient detre ajoute
                    commit("SETPRODUCTSHOLD", holds.data.data)

                }
            })

            .catch(function(error) {
                console.log('customer_create_error',error);
                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);
                // ...
            });
    },

    async addProductsHold({commit},data){

        axios.post(state.resource_url+"posales",
              {
                holdId:data.selecthold.holdId,
                sessionCaisseId:localStorage.getItem("sessioncaisse"),
                produitId:data.produit.produitId,
                status:1,
                reference:data.reference})

            .then(holds => {
                let test={}
                if(holds.data.success) {

                    test = holds.data.data
                    test.message = holds.data.message
                    test.statut = 0
                    test.success=true
                    console.log('est',holds.data.message)
                    //objet client qui vient detre ajoute


                }else{
                    test.message = holds.data.message
                    test.statut = 0
                    test.success=false
                    console.log('estE',holds.data.message)
                    //objet client qui vient detre ajoute

                }
                commit("SETADDPRODUCTSHOLD", test)

            })

            .catch(function(error) {
                console.log('customer_create_error',error.data);


                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);
                // ...
            });
    },

    async removeAllProductsHold({commit},data){

        //data.sessionCaisseId  = localStorage.getItem("sessioncaisse")
        axios.post(state.resource_url+"hold/refresh",data)

            .then(holds => {
                if(holds.data.success) {

                    commit("REMOVEALLPRODUCTSHOLD", true)
                    //objet client qui vient detre ajoute

                }

            })

            .catch(function(error) {
                console.log('customer_create_error',error.data);

            });
    },

    async removeProductsHold({commit},data){

        axios.post(state.resource_url+"deleteposale",
              {
                sessionCaisseId:localStorage.getItem("sessioncaisse"),
                reference:data.reference})

            .then(holds => {
                if(holds.data.success) {
                    let test = {}
                    console.log('sstatus',data)
                    test = holds.data
                    test.statut = 1
                    test.reference = data.reference
                    //holds.data.status = 1
                    //objet client qui vient detre ajoute
                    commit("SETADDPRODUCTSHOLD", test)

                }
            })

            .catch(function(error) {
                console.log('customer_create_error',error);
                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);
                // ...
            });
    },

    async closebox({commit},data){

        axios.post(state.resource_url+"fermerSessionCaisse",{"sessionCaisseId":localStorage.getItem("sessioncaisse"),"montantFermeture":data.ouvertureAmount})

            .then(holds => {
                console.log('status',holds)

                if(holds.data.success) {

                    //holds.data.status = 1
                    //objet client qui vient detre ajoute
                    commit("CLOSEBOX", true)

                }
            })

            .catch(function(error) {
                console.log('customer_create_error',error);
                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);
                // ...
            });

    },

    async checksession(){

        axios.get(state.resource_url+"sessioncaisseexist/"+state.client.id)

            .then(holds => {

                if(holds.data.success) {

                    localStorage.setItem("boxopen",false)
                    localStorage.setItem("boxshow",false)
                    localStorage.setItem("sessioncaisse",holds.data.data[0].sessionCaisseId)

                    this.dispatch('holdliste',{id:1})
                    //commit("OPENBOX",true)

                }else{
                    //this.holdliste({id:0})

                    localStorage.setItem("boxopen",true)
                    localStorage.setItem("boxshow",true)
                }
            })

            .catch(function(error) {
                console.log('customer_create_error',error);
                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);
                // ...
            });

    },

    async openboxs({commit},data){

        axios.post(state.resource_url+"ouverturecaisse",{
            "montantOuverture":data.ouvertureAmount,
            "userId":1,
        })

            .then(holds => {

                if(holds.data.success) {

                    commit("CLOSEBOX", false)

                    //stocker le hold id
                    state.session_caisse_id = holds.data.data.sessionCaisseId
                    //console.log('sessioncaisseid', state.session_caisse_id)
                    state.montantopen = holds.data.data.montantOuverture

                    localStorage.setItem("montantopen",holds.data.data.montantOuverture)
                    localStorage.setItem("sessioncaisse",holds.data.data.sessionCaisseId)
                    localStorage.setItem("boxopen",false)
                    this.dispatch('holdliste',{id:0})
                    //this.holdliste({id:0})


                }
            })

            .catch(function(error) {
                console.log('customer_create_error',error);
                // Handle Errors here.
                // var errorCode = error.code;
                // var errorMessage = error.message;
                // console.log(error);

                //commit("setError", error);
                // ...
            });

    }

};

const getters = {

    getCustomers: state => state.clients,
    getCustomer: state => state.client,
    getStatus: state => state.status,
    getObjetHold : state=>state.objethold,
    getStatusHold : state=>state.statusrequete,
    getHoldList : state => state.holdlist,
    getSelectHold : state => state.selecthold,
    GetProductsHold : state => state.listproducts,
    MoveProductsHold : state => state.productshold,
    GetBoxOpen : state => state.boxopen,
    GetCaisseOpen : state => state.caisseopen,
    GetRemoveAllProducts : state => state.removeallproducts


}

const mutations = {
    SETCUSTOMERS: (state,clients)=>state.clients=clients,
    SETCUSTOMER: (state,client)=>state.client=client,
    SETSTATUS: (state,status)=>state.status=status,
    SETNAMECLIENT : (state,client)=>state.clientName=client,
    SETOBJETHOLD : (state,objethold)=>state.objethold=objethold,
    SETSTATUSHOLD : (state,statusrequete)=>state.statusrequete=statusrequete,
    SETHOLDLIST : (state,holdlist)=>state.holdlist=holdlist,
    SETSELECTHOLD : (state,selecthold)=>state.selecthold=selecthold,
    SETPRODUCTSHOLD : (state,productshold)=>state.listproducts=productshold,
    SETADDPRODUCTSHOLD : (state,productshold)=>state.productshold=productshold,
    CLOSEBOX : (state,val)=>state.boxopen=val,
    OPENBOX : (state,val)=>state.caisseopen=val,
    REMOVEALLPRODUCTSHOLD : (state,val)=>state.removeallproducts=val

};


export default {
    state,getters,actions,mutations
}
